import React from 'react';
import useProvider from '../hooks/useProvider';

const Contantside = () => {
  const { note } = useProvider();

  return (
    <div className=" fixed right-20 top-40 bg-black min-w-[200px] max-w-[800px] p-6 rounded-2xl  w-full">
      <h2 className="text-2xl capitalize font-gloock">{note && note.title}</h2>
      <p className="mt-8 text-gray-300 text-xl">{note && note.note}</p>
    </div>
  );
};

export default Contantside;
