import React from 'react';

const Sidebar = ({ notes }) => {
  const clickHander = (note) => {
    dispatch({ type: 'CURRENT_NOTE', payload: note });
  };

  return (
    <div>
      <ul className="bg-black max-w-[300px] mt-20 p-5 rounded-2xl">
        <h1 className="text-2xl mb-4">Notes</h1>
        {notes &&
          notes.map((note) => (
            <button className="block w-full" onClick={() => clickHander(note)} key={note.id}>
              <h1 className=" font-cairo text-left text-xl cursor-pointer hover:bg-gray-600 transition-all  bg-gray-800  p-2 my-2 rounded-lg">{note.title}</h1>
            </button>
          ))}
      </ul>
    </div>
  );
};

export default Sidebar;
